# --- objective ---
def calculate_objective(item_box_assignments: DSList(length=9, elem_type=BoxAssignment)) -> int:
    max_box_id: int = 0
    for i in range(1, N_ITEMS + 1):
        if item_box_assignments[i].box_id > max_box_id:
            max_box_id = item_box_assignments[i].box_id
    return max_box_id

